using System;
using System.Configuration;
using System.Collections.Generic;
using System.Data.Entity;
using Microsoft.VisualStudio.TestTools.UnitTesting;

using lurin.meurhonline.application;
using lurin.meurhonline.domain.model;
using lurin.meurhonline.infrastructure.persistence;
using lurin.meurhonline.infrastructure.persistence.interfaces;

namespace lurin.meurhonline.application.Tests
{
    [TestClass]
    public class CartaoPontoFacadeTest
    {
        public static IUnitOfWorkCustom unitOfWork;
        public static CartaoPontoFacade cartaoPontoFacade;

        public CartaoPontoFacadeTest()
        {
            unitOfWork = new UnitOfWork();
            cartaoPontoFacade = new CartaoPontoFacade();
        }

        [TestMethod]
        [Ignore]
        public void AdicionarCartaoPonto_SUCESS()
        {
            var cartaoPontoModel = new CartaoPontoModel();
            cartaoPontoModel.DocumentoBase64 = " + ";

            var result = cartaoPontoFacade.AdicionarCartaoPonto(cartaoPontoModel);

            if (result == null)
                Assert.IsNull(result);
            else
                Assert.IsNotNull(result);
        }


        [TestMethod]
        public void BuscarCartaoPontoPorId_SUCESS()
        {
            int CartaoPontoId = 3;

            var result = cartaoPontoFacade.BuscarCartaoPontoPorId(CartaoPontoId);

            if (result == null)
                Assert.IsNull(result);
            else
                Assert.IsNotNull(result);
        }

        [TestMethod]
        public void BuscarCartaoPontoPorColaboradorId_SUCESS()
        {
            int ColabotadorId = 6;
            string Mes = "03";
            string Ano = "2021";

            var result = cartaoPontoFacade.BuscarCartaoPontoPorColaboradorIdMesAno(ColabotadorId, Mes, Ano);

            if (result == null)
                Assert.IsNull(result);
            else
                Assert.IsNotNull(result);
        }
    }
}

